$(document).ready(function() {
  // $(document).ready(function() { }): Ini adalah cara untuk menunggu sampai seluruh dokumen HTML selesai dimuat sebelum menjalankan kode JavaScript
    // Memberikan efek fadeIn pada gambar dengan class card-img-top
    $('.card-img-top').hide().fadeIn(1500); // Efek fadeIn selama 1.5 detik (1500 milidetik)
  });


